import React from 'react'
import {BrowserRouter as Router,Link, Switch} from 'react-router-dom';
import Route from 'react-router-dom/Route.js';
import Login from '../Login/Login';

import 'bootstrap/dist/css/bootstrap.min.css';
// import {Card} from "react-bootstrap";
import "./main.css";




function clickMe(){
alert('you clicked me ');
}



class  Main extends React.Component {
    

    render() {
      return (
        <Router>
        <div className='main'>  
            <div className='main-container'> 
              <div className='main-body'>
                <h1 className='main-title'>
                  We Cheer you Up With Our Scheduled Language Activities.
                </h1>
                  <div className='main-divtext'>
                    <p className='main-text'>
                     Welcome to  Speactivity! Here, people team up and make activites to learn a language together.
                    </p>
                  </div>
                  <div className="main-divbutton">
                    
                   <Link to='/Login'>Click to Register</Link>
                  </div>
              </div>
            </div>
        </div>
        <Switch>
          <Route path='/Login'>
            <Login/>
          </Route>
        </Switch>
        </Router>
    );
  }
  }
  
  export default Main;
